import axios from 'axios';
import React, { useEffect, useState } from 'react';
import UserService from '../../Services/UserService';
import './Produtos.css';
import Main from '../template/Main';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

//Import das imagens 
import img from '../../assets/ListaJogos/Mario.jpg';

const title = "Lista de Produtos";

const urlAPI = "http://localhost:5255/api/produto";

const initialState = {
    produto: { id_produto: 0, valor: 0.0, nome: '', descricao: '' },
    lista: [],
}


export default function AlterarProd(props) {

    const [lista, setLista] = useState([]);
    const [mens, setMens] = useState([]);

        /*    
            axios(urlAPI).then(resp => {
                this.setState({ lista: resp.data })
            })
        }
        */
        useEffect(() => {
            UserService.getAdmBoard().then(
                (response) => {
                    console.log("useEffect getAdmBoard: " + response.data)
                    setLista(response.data);
                    setMens(null);
                },
                (error) => {
                    const _mens =
                        (error.response &&
                            error.response.data &&
                            error.response.data.message) ||
                        error.message ||
                        error.toString();
                    setMens(_mens);
                    console.log("_mens: " + _mens);
                }
            );
        }, []);

/*   
        limpar() {
            this.setState({ aluno: initialState.aluno });
        }

        salvar() {
            let codigoCurso = document.getElementById('codigoCurso').value;
            const aluno = this.state.aluno;

            aluno.codCurso = Number(codigoCurso);
            const metodo = aluno.id ? 'put' : 'post';
            const url = aluno.id ? `${urlAPI}/${aluno.id}` : urlAPI;
            axios[metodo](url, aluno)
                .then(resp => {
                    const lista = this.getListaAtualizada(resp.data)
                    this.setState({ aluno: initialState.aluno, lista })
                })
        }


        getListaAtualizada(aluno, add = true) {
            const lista = this.state.lista.filter(a => a.id !== aluno.id);
            if (add) lista.unshift(aluno);
            return lista;
        }

        atualizaCampo(event) {
            //clonar usuário a partir do state, para não alterar o state diretamente
            const aluno = { ...this.state.aluno };
            //usar o atributo NAME do input para identificar o campo a ser atualizado
            aluno[event.target.name] = event.target.value;
            //atualizar o state
            this.setState({ aluno });
        }

        carregar(aluno) {
            this.setState({ aluno })
        }

        remover(aluno) {
            const url = urlAPI + "/" + aluno.id;
            if (window.confirm("Confirma remoção do aluno: " + aluno.ra)) {
                console.log("entrou no confirm");
                axios['delete'](url, aluno)
                    .then(resp => {
                        const lista = this.getListaAtualizada(aluno, false)
                        this.setState({ aluno: initialState.aluno, lista })
                    })
            }
        }


    const renderTable = () => {
            return (
                <div className="inclui-container">
                    <label> RA: </label>
                    <input
                        type="text"
                        id="ra"
                        placeholder="RA do aluno"
                        className="form-input"
                        name="ra"

                        value={this.state.aluno.ra}

                        onChange={e => this.atualizaCampo(e)}
                    />
                    <label> Nome: </label>
                    <input
                        type="text"
                        id="nome"
                        placeholder="Nome do aluno"
                        className="form-input"
                        name="nome"

                        value={this.state.aluno.nome}

                        onChange={e => this.atualizaCampo(e)}
                    />

                    <label> Código do Curso: </label>
                    <select name="nomeCurso" id="codigoCurso">
                        {this.state.listaCurso.map((curso) =>
                            <option key={curso.id} value={curso.codCurso}>{curso.nomeCurso}</option>
                        )}
                    </select>

                    <button className="btnSalvar"
                        onClick={e => this.salvar(e)} >
                        Salvar
                    </button>
                    <button className="btnCancelar"
                        onClick={e => this.limpar(e)} >
                        Cancelar
                    </button>
                </div>
            )
        }
*/
    const renderTable = () => {
        return (
            <div>
                {
                    this.state.lista.map((produto) =>
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" class="Img" src={img} />
                            <Card.Body>
                                <Card.Title><h3>{produto.nome}</h3></Card.Title>
                                <Card.Text>
                                    {produto.descricao}
                                </Card.Text>
                                <div>
                                    <Button className='btn' variant="secondary">Compre</Button>
                                </div>
                            </Card.Body>
                        </Card>
                    )
                }
            </div>
        )
    }       
            return (
                <Main className='main' title={title}>

                 
    {(mens) ? "Problema com conexão ou autorização (contactar administrador)." : renderTable()}


                </Main>
            )
        }
