import React, { Component } from 'react';
import axios from 'axios';
import './Produtos.css';
import Main from '../template/Main';

const title = "Lista de Produtos";

const urlAPI = "http://localhost:5255/api/aluno";
const urlAPICurso = "http://localhost:5255/api/curso";


const initialState = {
    aluno: { id: 0, ra: '', nome: '', codCurso: 0 },
    curso: { id: 0, codCurso: 0, nomeCurso: '', periodo: '' },
    lista: [],
    listaCurso: []
}


export default class Produtos extends Component {

    state = {...initialState}

    componentDidMount() {
        axios(urlAPI)
        .then(result => {
            this.setState({lista: result.data})
        });
        axios(urlAPICurso).then(resp => {
            this.setState({ listaCurso: resp.data })
        })
    }

    renderTable() {
        return (
            
            <div>
                {
                    this.state.lista.map((aluno) =>
                        <div className="card" key={aluno.id}>
                            <img src={''} alt={aluno.ra} />
                            <div className="container">
                                <h4><b>{aluno.ra}</b></h4>
                                <p>{aluno.nome}</p>
                            </div>
                        </div>
                    )
                }
            </div>
        )
    }

    render() {
        return (
            <Main title={title}>
                {this.renderTable()}
            </Main>
        )
    }
}
