import React, { Component } from 'react';
import axios from 'axios';
import './Carometro.css';
import Card from 'react-bootstrap/Card';
//import 'bootstrap'
import Main from '../template/Main';

const title = "Carometro";

const urlAPI = "http://localhost:5255/api/carometro";
const urlAPICurso = "http://localhost:5255/api/curso";
const urlAPIAluno = "http://localhost:5255/api/aluno";

const initialState = {
    aluno: { id: 0, ra: '', nome: '', codCurso: 0 },
    lista: []
}

export default class Carometro extends Component {

    state = {...initialState}

    componentDidMount() {
        axios(urlAPI)
        .then(result => {
            this.setState({lista: result.data})
        })
    }

    renderTable() {
        return (
            <div style={{width:120}}>
                {this.state.lista.map(
                    (aluno) =>
                <Card key={aluno.id} style={{marginTop:'10px',alignSelf:'center',justifyContent:'center'}}>
                    <Card.Body>
                        <Card.Title>ssasaas{aluno.ra}</Card.Title>
                        <Card.Text>
                            sasaasaa{aluno.nome}
                        </Card.Text>
                    
                    </Card.Body>
                </Card>
                )}
            </div>
        )
    }

    render() {
        return (
            <Main title={title}>
                {this.renderTable()}
            </Main>
        )
    }
}
